  `value_d` varchar(2000) DEFAULT NULL COMMENT '價值性',
  `feasibility_d` varchar(2000) DEFAULT NULL COMMENT '可行性',
  `market_survey` varchar(200) DEFAULT NULL COMMENT '市場搜尋',
