  `joint_person` varchar(11) DEFAULT NULL COMMENT '協辦窗口',
  `co_worker` varchar(50) DEFAULT NULL COMMENT '承作廠商',
  `idea_examination` varchar(3000) DEFAULT NULL COMMENT '提案審核進度',
  `Idea` varchar(11) DEFAULT NULL,
  `Requirement` varchar(11) DEFAULT NULL,
  `Feasibility` varchar(11) DEFAULT NULL,
  `Prototype` varchar(11) DEFAULT NULL,
  `note` varchar(50) DEFAULT NULL COMMENT '備註',
  `adoption` varchar(50) DEFAULT NULL COMMENT '導入車型',
  `applied_patent` varchar(50) DEFAULT NULL COMMENT '專利申請',
