  `resurrection_applied` varchar(20) DEFAULT NULL COMMENT '敗部復活申請與否',
  `PM_in_charge` varchar(11) DEFAULT NULL COMMENT '創意中心窗口',
  `closed_case` varchar(11) DEFAULT NULL COMMENT '結案',
  `is_blocked` int(10) unsigned NOT NULL DEFAULT '2' COMMENT '是否被鎖住(1:鎖住;2:未鎖住)',
  `current_user` varchar(30) DEFAULT NULL COMMENT '正在編輯的使用者',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=7859 ;

